// GSU Themed AI Chatbot - Final Professional Version
// Updated: Clean UI, Gradient Background, Drawer Suggestions

import React, { useState, useRef, useEffect } from "react";
import {
  FaSmile,
  FaTrash,
  FaPaperPlane,
  FaDownload,
  FaMoon,
  FaSun,
  FaMicrophone,
} from "react-icons/fa";
import pantherLogo from "./assets/logo.png";

const quickReplies = [
  "How to Apply",
  "Application Deadlines",
  "Other Deadlines",
  "Tuition and Fees",
  "Programs Available",
  "Courses Available",
  "Office Contacts",
];

const languages = ["English", "Spanish", "French", "Chinese"];

function Chatbot() {
  const [messages, setMessages] = useState(() => {
    const stored = localStorage.getItem("chat_messages");
    return stored
      ? JSON.parse(stored)
      : [
          {
            sender: "bot",
            text: "Hello! ðŸ‘‹ I'm Pounce, your GSU chatbot. How can I help you today?",
          },
          {
            sender: "bot",
            text: "Which one would you like to learn about?",
          },
        ];
  });

  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [showEmoji, setShowEmoji] = useState(false);
  const [language, setLanguage] = useState("English");
  const [ttsEnabled, setTtsEnabled] = useState(false);

  const endRef = useRef(null);
  const emojiRef = useRef(null);

  useEffect(() => {
    const picker = emojiRef.current;
    if (picker) {
      const handleEmojiClick = (e) => {
        const emoji = e.detail.unicode;
        setInput((prev) => prev + emoji);
        setShowEmoji(false);
      };
      picker.addEventListener("emoji-click", handleEmojiClick);
      return () => picker.removeEventListener("emoji-click", handleEmojiClick);
    }

    endRef.current?.scrollIntoView({ behavior: "smooth" });
    localStorage.setItem("chat_messages", JSON.stringify(messages));

    const handleEmoji = (e) => {
      if (e.target.tagName === "EMOJI-PICKER") return;
      if (showEmoji && !emojiRef.current?.contains(e.target)) {
        setShowEmoji(false);
      }
    };

    document.addEventListener("click", handleEmoji);
    return () => document.removeEventListener("click", handleEmoji);
  }, [messages, showEmoji]);

  const handleSend = (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    setMessages((prev) => [...prev, { sender: "user", text: input }]);
    setInput("");
    simulateBotReply(`Great! Here's info about "${input}".`);
  };

  const simulateBotReply = async (text) => {
    setIsTyping(true);
    let reply = text;
    let translated = text;

    if (language !== "English") {
      setMessages((prev) => [...prev, { sender: "bot", text: "Translating..." }]);
      try {
        translated = await translateText(text, language);
      } catch (err) {
        console.error("Translation failed:", err);
      }
    }

    setMessages((prev) => prev.filter(m => m.text !== "Translating..."));
    setTimeout(() => {
      setMessages((prev) => [...prev, { sender: "bot", text: translated }]);
      if (ttsEnabled) speakText(translated);
      setIsTyping(false);
    }, 1200);
  };

  const translateText = async (text, targetLang) => {
    if (!targetLang || targetLang === "English") return text;
    try {
      const res = await fetch("https://libretranslate.de/translate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          q: text,
          source: "en",
          target: targetLang.toLowerCase().slice(0, 2),
          format: "text",
        }),
      });
      const data = await res.json();
      return data.translatedText;
    } catch (error) {
      console.error("Translation failed:", error);
      return text;
    }
  };

  const speakText = (text) => {
    const synth = window.speechSynthesis;
    if (!synth) return;
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = language === "English" ? "en-US" : language;
    synth.speak(utterance);
  };

  const handleQuickReply = (reply) => {
    setMessages((prev) => [...prev, { sender: "user", text: reply }]);
    simulateBotReply(`Here's some information about "${reply}".`);
  };

  const handleVoiceInput = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Speech recognition not supported in this browser.");
      return;
    }
    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;
    recognition.start();

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setInput((prev) => prev + transcript);
    };
    recognition.onerror = (event) => {
      console.error("Speech recognition error", event);
    };
  };

  return (
    <div className={`${darkMode ? "dark" : ""}`}>
      <div className="flex justify-center items-center min-h-screen bg-gradient-to-tr from-[#c3dafe] via-white to-[#d1fae5] dark:from-[#1e293b] dark:via-[#0f172a] dark:to-[#1e293b] px-4 transition-all">
        <div className="w-full max-w-md bg-white dark:bg-gray-800 rounded-3xl shadow-2xl border border-gray-200 dark:border-gray-700 overflow-hidden relative">

          {/* Header */}
          <div className="bg-gradient-to-r from-blue-700 to-indigo-900 dark:from-gray-700 dark:to-gray-900 p-4 flex items-center justify-between">
            <div className="flex items-center gap-3">
              <img src={pantherLogo} alt="GSU Panther" className="h-10 w-10 drop-shadow-md" />
              <h1 className="text-white font-bold text-xl">Pounce</h1>
            </div>
            <select
              className="bg-transparent outline-none text-white text-sm"
              value={language}
              onChange={(e) => setLanguage(e.target.value)}
            >
              {languages.map((lang) => (
                <option key={lang} value={lang} className="text-black">{lang}</option>
              ))}
            </select>
          </div>

          {/* Messages */}
          <div className="h-[440px] overflow-y-auto px-4 py-4 bg-gray-50 dark:bg-gray-900 space-y-3 text-sm">
            {messages.map((msg, i) => (
              <div key={i} className={`flex ${msg.sender === "user" ? "justify-end" : "justify-start"}`}>
                <div className="flex items-start gap-2">
                  {msg.sender === "bot" && <div className="text-xl">ðŸ¤–</div>}
                  <div className={`px-4 py-2 rounded-2xl shadow ${msg.sender === "user" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800 dark:bg-gray-700 dark:text-white"}`}>
                    {msg.text}
                  </div>
                  {msg.sender === "user" && <div className="text-xl">ðŸ‘¤</div>}
                </div>
              </div>
            ))}
            {isTyping && (
              <div className="text-gray-500 dark:text-gray-300 italic animate-pulseFast">Pounce is typing...</div>
            )}
            <div ref={endRef} />
          </div>

          {/* Input */}
          <form
            onSubmit={handleSend}
            className="flex items-center px-4 py-4 border-t bg-white dark:bg-gray-800 gap-2"
          >
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Ask me a question..."
              className="flex-1 px-4 py-2 rounded-xl border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-900 text-gray-800 dark:text-white focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
            />
            <button
              type="button"
              onClick={handleVoiceInput}
              className="text-blue-600 hover:text-blue-800 dark:text-blue-300 dark:hover:text-blue-500 text-xl transition hover:scale-110"
            >
              <FaMicrophone />
            </button>
            <button
              type="submit"
              className="text-blue-600 hover:text-blue-800 dark:text-blue-300 dark:hover:text-blue-500 text-xl transition hover:scale-110"
            >
              <FaPaperPlane />
            </button>
          </form>

          {/* Suggestions Drawer Toggle */}
          <div className="absolute bottom-2 left-2">
            <details className="group">
              <summary className="bg-blue-600 text-white px-4 py-2 rounded-lg cursor-pointer hover:bg-blue-700 shadow-md transition">
                Suggestions
              </summary>
              <div className="mt-2 flex flex-col gap-2 bg-white dark:bg-gray-800 p-3 rounded-xl shadow-lg border border-gray-200 dark:border-gray-700 max-w-[250px]">
                {quickReplies.map((text, idx) => (
                  <button
                    key={idx}
                    onClick={() => handleQuickReply(text)}
                    className="border border-blue-500 dark:border-blue-400 text-blue-600 dark:text-blue-300 px-3 py-1 rounded hover:bg-blue-50 dark:hover:bg-gray-700 transition text-sm"
                  >
                    {text}
                  </button>
                ))}
              </div>
            </details>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chatbot;
